clear all;close all;clc;addpath('../funciones')legajo = 022386;[n, x] = hcanald(legajo);% Identificar valores diferentes de cerovalores_diferentes_de_cero = x ~= 0; % Vector logico para x != 0valores_cero = ~valores_diferentes_de_cero; % Complemento (x == 0)% Plot de la senal con colores personalizadosfigure;hold on; % Permite superponer graficos% Trazar los valores iguales a cero en azulstem(n(valores_cero), x(valores_cero), 'o', 'Color', 'b');% Trazar los valores diferentes de cero en rojostem(n(valores_diferentes_de_cero), x(valores_diferentes_de_cero), 'o', 'Color', 'r');% Configurar el graficotitle('Senal');xlabel('n');ylabel('x[n]');legend({'Valores = 0', 'Valores != 0'});% Mostrar indices, valores y alturas diferentes de cerodisp('Indices, valores diferentes de cero y alturas:');indices_diferentes_de_cero = n(valores_diferentes_de_cero);valores_diferentes_de_cero = x(valores_diferentes_de_cero);alturas_diferentes_de_cero = abs(valores_diferentes_de_cero); % Altura como valor absolutofor i = 1:length(indices_diferentes_de_cero)    fprintf('n = %d, x[n] = %.2f, Altura = %.2f\n', ...        indices_diferentes_de_cero(i), valores_diferentes_de_cero(i), alturas_diferentes_de_cero(i));end