clear all;close all;clc;addpath('./funciones')legajo = 022386;[n, x] = senial(legajo);% Plot de la Senialfigure(1);stem(n, x, 'o');title('Señal');xlabel('n');ylabel('x[n]');ds = 0.001;s = [-2:ds:2];TFTD_S = TFTD_senial(x, n, s);m = abs(TFTD_S);p = unwrap(angle(TFTD_S));% Plot del modulo de la TFTDfigure(2);set(gcf, 'Position', [100, 100, 1200, 400]); % [x, y, ancho, alto]subplot(1, 2, 1);plot(s, m);title('Módulo de la TFTD');xlabel('s');ylabel('|X(e^{j2\pi s})|');subplot(1, 2, 2);plot(s, m);title('Módulo de la TFTD');xlabel('s');ylabel('|X(e^{j2\pi s})|');xlim([-0.5, 0.5]);% Plot de la fase de la TFTDfigure(3);subplot(1, 1, 1);plot(s, p);title('Fase de la TFTD');xlabel('s');ylabel('\angle X(e^{j2\pi s})');