clear all;close all;clc;addpath('../funciones')% Definición de parametrosn = 0:100;                % Eje temporal discretods = 0.001;               % Incremento en frecuencias = -0.5:ds:0.5;          % Frecuencia normalizada% Analisis de Sistemas% Sistema Unoh_1 = irf_sys_1(n);       % Respuesta al impulso del Sistema UnoH_1 = frf_sys_1(s);       % Respuesta en frecuencia del Sistema Uno% Sistema Dosh_2 = irf_sys_2(n);       % Respuesta al impulso del Sistema DosH_2 = frf_sys_2(s);       % Respuesta en frecuencia del Sistema Dos% Sistema Tresh_3 = irf_sys_3(n);       % Respuesta al impulso del Sistema TresH_3 = frf_sys_3(s);       % Respuesta en frecuencia del Sistema Tres% Sistema Cuatroh_4 = irf_sys_4(n);       % Respuesta al impulso del Sistema CuatroH_4 = frf_sys_4(s);       % Respuesta en frecuencia del Sistema Cuatro% Graficas de Funciones% Sistema Uno% Respuesta al Impulsofigure;stem(n, h_1, 'o');title('Respuesta al Impulso h_1[n]');xlabel('n');ylabel('h_1[n]');grid on;% Figura conjunta para el Módulo y la Fase de la Respuesta en Frecuenciafigure;set(gcf, 'Position', [100, 100, 1200, 400]); % Ajustar posición y tamaño de la figura% Subgráfica para el Módulosubplot(1, 2, 1);plot(s, abs(H_1), 'LineWidth', 1.5);title('Respuesta en Frecuencia (Módulo) |H_1(e^{j2\pi s})|');xlabel('Frecuencia Normalizada (s)');ylabel('|H_1(e^{j2\pi s})|');grid on;% Subgráfica para la Fasesubplot(1, 2, 2);plot(s, unwrap(angle(H_1)), 'LineWidth', 1.5);title('Respuesta en Frecuencia (Fase) \phi(H_1(e^{j2\pi s}))');xlabel('Frecuencia Normalizada (s)');ylabel('\phi(H_1(e^{j2\pi s})) [radianes]');grid on;% Sistema Dos% Respuesta al Impulsofigure;stem(n, h_2, 'o');title('Respuesta al Impulso h_2[n]');xlabel('n');ylabel('h_2[n]');grid on;% Figura conjunta para el Módulo y la Fase de la Respuesta en Frecuenciafigure;set(gcf, 'Position', [100, 100, 1200, 400]); % Ajustar posición y tamaño de la figura% Subgráfica para el Módulosubplot(1, 2, 1);plot(s, abs(H_2), 'LineWidth', 1.5);title('Respuesta en Frecuencia (Módulo) |H_2(e^{j2\pi s})|');xlabel('Frecuencia Normalizada (s)');ylabel('|H_2(e^{j2\pi s})|');grid on;% Subgráfica para la Fasesubplot(1, 2, 2);plot(s, unwrap(angle(H_2)), 'LineWidth', 1.5);title('Respuesta en Frecuencia (Fase) \phi(H_2(e^{j2\pi s}))');xlabel('Frecuencia Normalizada (s)');ylabel('\phi(H_2(e^{j2\pi s})) [radianes]');grid on;% Sistema Tres% Respuesta al Impulsofigure;stem(n, h_3, 'o');title('Respuesta al Impulso h_3[n]');xlabel('n');ylabel('h_3[n]');grid on;% Figura conjunta para el Módulo y la Fase de la Respuesta en Frecuenciafigure;set(gcf, 'Position', [100, 100, 1200, 400]); % Ajustar posición y tamaño de la figura% Subgráfica para el Módulosubplot(1, 2, 1);plot(s, abs(H_3), 'LineWidth', 1.5);title('Respuesta en Frecuencia (Módulo) |H_3(e^{j2\pi s})|');xlabel('Frecuencia Normalizada (s)');ylabel('|H_3(e^{j2\pi s})|');grid on;% Subgráfica para la Fasesubplot(1, 2, 2);plot(s, unwrap(angle(H_3)), 'LineWidth', 1.5);title('Respuesta en Frecuencia (Fase) \phi(H_3(e^{j2\pi s}))');xlabel('Frecuencia Normalizada (s)');ylabel('\phi(H_3(e^{j2\pi s})) [radianes]');grid on;% Sistema Cuatro% Respuesta al Impulsofigure;stem(n, h_4, 'o');title('Respuesta al Impulso h_4[n]');xlabel('n');ylabel('h_4[n]');grid on;% Figura conjunta para el Módulo y la Fase de la Respuesta en Frecuenciafigure;set(gcf, 'Position', [100, 100, 1200, 400]); % Ajustar posición y tamaño de la figura% Subgráfica para el Módulosubplot(1, 2, 1);plot(s, abs(H_4), 'LineWidth', 1.5);title('Respuesta en Frecuencia (Módulo) |H_4(e^{j2\pi s})|');xlabel('Frecuencia Normalizada (s)');ylabel('|H_4(e^{j2\pi s})|');grid on;% Subgráfica para la Fasesubplot(1, 2, 2);plot(s, unwrap(angle(H_4)), 'LineWidth', 1.5);title('Respuesta en Frecuencia (Fase) \phi(H_4(e^{j2\pi s}))');xlabel('Frecuencia Normalizada (s)');ylabel('\phi(H_4(e^{j2\pi s})) [radianes]');grid on;