clear all;close all;clc;addpath('../funciones');% Definicion de parametrosn = 0:100;                % Eje temporal discretods = 0.001;               % Incremento en frecuencias = -0.5:ds:0.5;          % Frecuencia normalizadad_k = delta(n);           % Delta de Kronecker% Analisis de Sistemas% Sistema Uno% Analiticasha_1 = irf_sys_1(n);Ha_1 = frf_sys_1(s);% Sistemah_1 = sys_1(d_k);H_1 = TFTD(h_1);% Sistema Dos% Analiticasha_2 = irf_sys_2(n);Ha_2 = frf_sys_2(s);% Sistemah_2 = sys_2(d_k);H_2 = TFTD(h_2);% Sistema Tres% Analiticasha_3 = irf_sys_3(n);Ha_3 = frf_sys_3(s);% Sistemah_3 = sys_3(d_k);H_3 = TFTD(h_3);% Sistema Cuatro% Analiticasha_4 = irf_sys_4(n);Ha_4 = frf_sys_4(s);% Sistemah_4 = sys_4(d_k);H_4 = TFTD(h_4);% Graficas de Funciones% Respuesta impulsional del Sistema Unofigure;subplot(2, 1, 1);stem(n, ha_1, 'o');title('Respuesta Impulsional Analitica del Sistema Uno');xlabel('n');ylabel('h_1[n]');subplot(2, 1, 2);stem(n, h_1, 'o');title('Respuesta Impulsional del Sistema Uno [Delta]');xlabel('n');ylabel('h_1[n]');% Respuesta en frecuencia del Sistema Uno (Modulo y Fase)figure;subplot(2, 2, 1);plot(s, abs(Ha_1));title('Respuesta en Frecuencia Analitica del Sistema Uno [Modulo]');xlabel('s');ylabel('|H_1(e^{j2pi s})|');subplot(2, 2, 2);plot(s, unwrap(angle(Ha_1)));title('Respuesta en Frecuencia Analitica del Sistema Uno [Fase]');xlabel('s');ylabel('phi(H_1(e^{j2pi s}))');subplot(2, 2, 3);plot(s, abs(H_1));title('Respuesta en Frecuencia del Sistema Uno [Delta] [Modulo]');xlabel('s');ylabel('|H_1(e^{j2pi s})|');subplot(2, 2, 4);plot(s, unwrap(angle(H_1)));title('Respuesta en Frecuencia del Sistema Uno [Delta] [Fase]');xlabel('s');ylabel('phi(H_1(e^{j2pi s}))');% Respuesta impulsional del Sistema Dosfigure;subplot(2, 1, 1);stem(n, ha_2, 'o');title('Respuesta Impulsional Analitica del Sistema Dos');xlabel('n');ylabel('h_2[n]');subplot(2, 1, 2);stem(n, h_2, 'o');title('Respuesta Impulsional del Sistema Dos [Delta]');xlabel('n');ylabel('h_2[n]');% Respuesta en frecuencia del Sistema Dos (Modulo y Fase)figure;subplot(2, 2, 1);plot(s, abs(Ha_2));title('Respuesta en Frecuencia Analitica del Sistema Dos [Modulo]');xlabel('s');ylabel('|H_2(e^{j2pi s})|');subplot(2, 2, 2);plot(s, unwrap(angle(Ha_2)));title('Respuesta en Frecuencia Analitica del Sistema Dos [Fase]');xlabel('s');ylabel('phi(H_2(e^{j2pi s}))');subplot(2, 2, 3);plot(s, abs(H_2));title('Respuesta en Frecuencia del Sistema Dos [Delta] [Modulo]');xlabel('s');ylabel('|H_2(e^{j2pi s})|');subplot(2, 2, 4);plot(s, unwrap(angle(H_2)));title('Respuesta en Frecuencia del Sistema Dos [Delta] [Fase]');xlabel('s');ylabel('phi(H_2(e^{j2pi s}))');% Respuesta impulsional del Sistema Tresfigure;subplot(2, 1, 1);stem(n, ha_3, 'o');title('Respuesta Impulsional Analitica del Sistema Tres');xlabel('n');ylabel('h_3[n]');subplot(2, 1, 2);stem(n, h_3, 'o');title('Respuesta Impulsional del Sistema Tres [Delta]');xlabel('n');ylabel('h_3[n]');% Respuesta en frecuencia del Sistema Tres (Modulo y Fase)figure;subplot(2, 2, 1);plot(s, abs(Ha_3));title('Respuesta en Frecuencia Analitica del Sistema Tres [Modulo]');xlabel('s');ylabel('|H_3(e^{j2pi s})|');subplot(2, 2, 2);plot(s, unwrap(angle(Ha_3)));title('Respuesta en Frecuencia Analitica del Sistema Tres [Fase]');xlabel('s');ylabel('phi(H_3(e^{j2pi s}))');subplot(2, 2, 3);plot(s, abs(H_3));title('Respuesta en Frecuencia del Sistema Tres [Delta] [Modulo]');xlabel('s');ylabel('|H_3(e^{j2pi s})|');subplot(2, 2, 4);plot(s, unwrap(angle(H_3)));title('Respuesta en Frecuencia del Sistema Tres [Delta] [Fase]');xlabel('s');ylabel('phi(H_3(e^{j2pi s}))');% Respuesta impulsional del Sistema Cuatrofigure;subplot(2, 1, 1);stem(n, ha_4, 'o');title('Respuesta Impulsional Analitica del Sistema Cuatro');xlabel('n');ylabel('h_4[n]');subplot(2, 1, 2);stem(n, h_4, 'o');title('Respuesta Impulsional del Sistema Cuatro [Delta]');xlabel('n');ylabel('h_4[n]');% Respuesta en frecuencia del Sistema Cuatro (Modulo y Fase)figure;subplot(2, 2, 1);plot(s, abs(Ha_4));title('Respuesta en Frecuencia Analitica del Sistema Cuatro [Modulo]');xlabel('s');ylabel('|H_4(e^{j2pi s})|');subplot(2, 2, 2);plot(s, unwrap(angle(Ha_4)));title('Respuesta en Frecuencia Analitica del Sistema Cuatro [Fase]');xlabel('s');ylabel('phi(H_4(e^{j2pi s}))');subplot(2, 2, 3);plot(s, abs(H_4));title('Respuesta en Frecuencia del Sistema Cuatro [Delta] [Modulo]');xlabel('s');ylabel('|H_4(e^{j2pi s})|');subplot(2, 2, 4);plot(s, unwrap(angle(H_4)));title('Respuesta en Frecuencia del Sistema Cuatro [Delta] [Fase]');xlabel('s');ylabel('phi(H_4(e^{j2pi s}))');