clear all;close all;clc;addpath('../funciones')% Definicion de parametrosn = 0:100;                % Eje temporal discretods = 0.001;               % Incremento en frecuencias = -0.5:ds:0.5;          % Frecuencia normalizadad_k = delta(n);           % Delta de Kronecker% Analisis de Sistemas% Sistema Uno% Analiticasha_1 = irf_sys_1(n);Ha_1 = frf_sys_1(s);% Sistemah_1 = sys_1(d_k);H_1 = TFTD(h_1);% Sistema Dosha_2 = irf_sys_2(n);Ha_2 = frf_sys_2(s);h_2 = sys_2(d_k);H_2 = TFTD(h_2);% Sistema Tresha_3 = irf_sys_3(n);Ha_3 = frf_sys_3(s);h_3 = sys_3(d_k);H_3 = TFTD(h_3);% Sistema Cuatroha_4 = irf_sys_4(n);Ha_4 = frf_sys_4(s);h_4 = sys_4(d_k);H_4 = TFTD(h_4);% Graficas de Funciones% Respuesta impulsional analítica del Sistema 1figure;stem(n, ha_1, 'o');title('Respuesta Impulsional Analítica del Sistema 1');xlabel('n');ylabel('h_1[n]');yticks(-1:0.1:1);% Respuesta impulsional ingresando una delta al Sistema 1figure;stem(n, h_1, 'o');title('Respuesta Impulsional del Sistema 1 ingresando una delta');xlabel('n');ylabel('h_1[n]');yticks(-1:0.1:1);% Respuesta en frecuencia analítica del Sistema 1 (Módulo)figure;plot(s, abs(Ha_1));title('Respuesta en Frecuencia Analítica del Sistema 1 [Módulo]');xlabel('s');ylabel('|H_1(e^{j2\pi s})|');% Respuesta en frecuencia analítica del Sistema 1 (Fase)figure;plot(s, unwrap(angle(Ha_1)));title('Respuesta en Frecuencia Analítica del Sistema 1 [Fase]');xlabel('s');ylabel('\phi(H_1(e^{j2\pi s}))');% Respuesta en frecuencia del Sistema 1 ingresando una delta (Módulo)figure;plot(s, abs(H_1));title('Respuesta en Frecuencia del Sistema 1 ingresando una delta [Módulo]');xlabel('s');ylabel('|H_1(e^{j2\pi s})|');% Respuesta en frecuencia del Sistema 1 ingresando una delta (Fase)figure;plot(s, unwrap(angle(H_1)));title('Respuesta en Frecuencia del Sistema 1 ingresando una delta [Fase]');xlabel('s');ylabel('\phi(H_1(e^{j2\pi s}))');