clear all;close all;clc;addpath('../funciones');legajo = 022386;[n, x] = senial(legajo);ds = 0.001;s = -0.5:ds:0.5;y4 = sys_4(x);Y4 = TFTD(y4);figure;subplot(2,2,4);stem(n,y4,'o');title('Salida del Sistema 4');xlabel('n');ylabel('y4[n]');figure;subplot(2,1,1);plot(s,abs(Y4));title('TFTD de la salida del Sistema 4 [Módulo]');ylabel('|Y4(e^j^2^π^s)|');subplot(2,1,2);plot(s,unwrap(angle(Y4)));title('TFTD de la salida del Sistema 4 [Fase]');ylabel('Φ(Y4(e^j^2^π^s)');